ruleExplore.bookList:
class.b module@tag.li!-1||$..[?(@.novelName)]||@JSon:$..[?(@.novelName)]&&$..[?(@.novelname)]&&$.massage&&class.b module@tag.li!-1
<js>
function remove(a,b){
let delIndexList = a; // 要删除的序列
 if (delIndexList.length !== 0) { 
for (let i = delIndexList.length - 1; i >= 0; i--) { b.splice(delIndexList[i], 1) } 
return b
}
}

if(baseUrl=='https://app-cdn.jjwxc.net/bookstore/getFullPage?channel=bhxs&version=3'||baseUrl.match(/novelfree/)){
list=JSON.parse(src);
for(i in list){
channelName=list[i].channelName;
blist=list[i].data;
if(blist!="undefined"){
for(j in blist){
blist[j].channelName=channelName
}}else{blist.channelName=channelName}

}
if(!baseUrl.match(/novelfree/)){
list=remove([0, 6,11,12,13],list)
}else{
list=remove([1],list)
}
result=JSON.stringify(list);}
else if(baseUrl.match(/signIn/)){
result="{signIn:["+src+"]}"
}
else{result=result}
</js>
$..[?(@.novelName)]&&$..[?(@.novelname)]&&$.signIn[*]

