ruleToc.chapterName:
$.chaptername
<js>
result=result.replace(/^\s+/,'')
intro=java.getString("$.chapterintro");
java.put("intro",intro);
vip='{{$.isvip}}'!='0';
lock='{{$.islock}}'!='0';
type='{{$.chaptertype}}'=='1';
num=!type?'{{$.chapterid}}｜':'';
if(result.match(/[一二三四五六七八九十百千万\d]+\s*章|^\d+[、\.\s]|chapter\s*\d+/i)){
num=''
}else{num=num}
result=type?'📖['+result+']📖':result;
v=vip?'✿·':'';
l=lock?'[此章节已锁]':'';
result=v+num+result+l;
</js>
<js>result.replace(/(\d+)｜\s*$/,'第$1章').replace(/\d+｜第(.+)章/,'第$1章').replace(/\d+\｜Chapter/,'Chapter').replace(/✿·📖/,'📖')</js>

