.authi a:lt(2),.authicn,.authi em,.t_f@html
<js>
lzs=source.getVariable()?source.getVariable():'';
lzb=book.getVariable("custom")?book.getVariable("custom"):'';
html=result.replace(/<\/td>\s*(<a|<img)/g,'</td>◎◎◎◎$1');
lznum=baseUrl.match(/page(?:%3D|=)1/)?true:false;
html=html.replace(/<\/*td/g,'<li').replace(/<i.*?<\/i><br>/g,'');
list=html.split("◎◎◎◎");
h="";
for(i in list){
	java.setContent(list[i]);	author=java.getString("@@class.xi2@text")!=""?java.getString("@@class.xi2@text"):"匿名青花鱼";
time=java.getString("@@tag.em@text");
bName=java.getString("@@blockquote@tag.font.0@text")+"\n";
re=String(author).replace(/发表于.*/,'');
bName=String(bName).indexOf(re)>-1?"":bName;
bText=java.getString("@@blockquote@textNodes||blockquote@tag.font.-1@textNodes")
bText=bText!=""?"回复:『"+bName+bText+"』":"";
lz=java.getString("@@tag.img@html").match(/ico_lz/)?"👤 ":"💬 ";
lz=(i==0 && lznum)?"👤 ":lz


j=org.jsoup.Jsoup.parse(list[i])
	j.select("blockquote").remove();
	tt=j.select(".t_f").html(); 

	if(lzs.indexOf("只看楼主")>-1||lzb.indexOf("只看楼主")>-1){
		     if(lz.indexOf("👤 ")>-1){
		     	h+=tt+"\n"
		  }
		}else{	h+=lz+"【"+author+"】"+time+"\n"+"\n"+bText+tt+"\n&lrm;\n"  	
			}
	}
h
</js>

