ruleToc.nextTocUrl:
@js:
try{
var n=String(result).match(/第<span>1[^\d]+(\d+)/)[1];
list=[]
if(n==1){
list;}else{
for(i=2;i<=n;i++){
list.push(baseUrl+i+".html")
};}list
}catch(e){}

ruleToc.chapterList:
@js:
//获取开始章节的id
start=java.getString('@@class.chapterList@tag.li.0@a@href').match(/(\d+)\.html/)[1];

//获取总章节数
page=result.match(/共(\d+)章节/)[1];
list=[];

//利用总章节，拼接目录
for(i=0;i<page;i++){
id=Number(start)+i;
list.push({text:String('第'+Number(i+1)+'页'),href:baseUrl.replace(/\.html/,'/'+id+".html")})
}
list

